import React, { Component } from 'react'
import Square from '../components/Square'

const Board = (props) => {
    const renderSquare = (i) => {
        return (
            <Square 
                onClick = {() => props.onClick(i)}
                value = {props.squares[i]}
            />
        )
    }

    return (
        <div>
            <div className = 'row'>
                {renderSquare(0)}
                {renderSquare(1)}
                {renderSquare(2)}
            </div>
            <div className = 'row'>
                {renderSquare(3)}
                {renderSquare(4)}
                {renderSquare(5)}
            </div>
            <div className = 'row'>
                {renderSquare(6)}
                {renderSquare(7)}
                {renderSquare(8)}
            </div>
        </div>
    )
}

export default Board;



